import React, { useState } from 'react';
import './App.css'; // Optional for styling

function App() {
  const [countryName, setCountryName] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handlePredict = async () => {
    setResult(null);
    setError(null);

    if (!countryName) {
      setError('Please enter a country name.');
      return;
    }

    try {
      // Make the GET request to the Flask API
      const response = await fetch(`http://127.0.0.1:5000/predict?country=${encodeURIComponent(countryName.toLowerCase())}`, {
        method: 'GET',
      });

      const data = await response.json();

      if (response.ok) {
        // Format predictions: Show only the first 10 and add units (if applicable)
        const formattedPredictions = data.predictions.slice(0, 10).map((value, index) => (
          `Day ${index + 1}: ${value.toFixed(2)} °C`
        )).join('\n');

        setResult(`Predicted data for ${data.country}:\n${formattedPredictions}`);
      } else {
        setError(data.error || 'An error occurred while fetching the prediction.');
      }
    } catch (err) {
      setError('Unable to connect to the server. Please try again later.');
    }
  };

  return (
    <div className="container">
      <h1>Weather Prediction</h1>
      <div className="form-group">
        <label htmlFor="country">Enter Country Name:</label>
        <input
          type="text"
          id="country"
          value={countryName}
          onChange={(e) => setCountryName(e.target.value)}
          placeholder="Country Name"
        />
        <button onClick={handlePredict}>Get Prediction</button>
      </div>

      {result && <div className="result">{result}</div>}
      {error && <div className="error">{error}</div>}
    </div>
  );
}

export default App;
