import React, { useState } from 'react';
import './App.css';

function App() {
  const [countryName, setCountryName] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handlePredict = async () => {
    setResult(null);
    setError(null);

    if (!countryName) {
      setError('Please enter a country name.');
      return;
    }

    try {
      const response = await fetch(`http://127.0.0.1:5000/predict?country=${encodeURIComponent(countryName.toLowerCase())}`, {
        method: 'GET',
      });

      const data = await response.json();

      if (response.ok) {
        // Format the result with each value on a new line
        const { average, max, min } = data.summary;
        setResult(`Average Temperature: ${average}\nMaximum Temperature: ${max}\nMinimum Temperature: ${min}`);
      } else {
        setError(data.error || 'An error occurred while fetching the prediction.');
      }
    } catch (err) {
      setError('Unable to connect to the server. Please try again later.');
    }
  };

  return (
    <div className="container">
      <h1>Weather Prediction</h1>
      <div className="form-group">
        <label htmlFor="country">Enter Country Name:</label>
        <input
          type="text"
          id="country"
          value={countryName}
          onChange={(e) => setCountryName(e.target.value)}
          placeholder="Country Name"
        />
        <button onClick={handlePredict}>Get Prediction</button>
      </div>

      {result && (
        <div className="result">
          <h3>Predicted Data</h3>
          {/* Option 1: Use <pre> to render newlines */}
          <pre>{result}</pre>

          {/* Option 2: Use <br> for more control */}
          {/* <div dangerouslySetInnerHTML={{ __html: result.replace(/\n/g, '<br>') }}></div> */}
        </div>
      )}

      {error && <div className="error">{error}</div>}
    </div>
  );
}

export default App;
