import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [countryName, setCountryName] = useState('');
  const [countries, setCountries] = useState([]);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  // Fetch the list of countries when the component loads
  useEffect(() => {
    const fetchCountries = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/countries');
        const data = await response.json();
        if (response.ok) {
          setCountries(data.countries); // Populate dropdown with countries
        } else {
          setError(data.error || 'Failed to fetch countries.');
        }
      } catch (err) {
        setError('Unable to connect to the server. Please try again later.');
      }
    };

    fetchCountries();
  }, []);

  const handlePredict = async () => {
    setResult(null);
    setError(null);

    if (!countryName) {
      setError('Please select a country.');
      return;
    }

    try {
      const response = await fetch(`http://127.0.0.1:5000/predict?country=${encodeURIComponent(countryName)}`, {
        method: 'GET',
      });

      const data = await response.json();

      if (response.ok) {
        // Format the result with each value on a new line
        const { average, max, min } = data.summary;
        setResult(`Predicted data for ${data.country}:\nAverage Temperature: ${average}\nMaximum Temperature: ${max}\nMinimum Temperature: ${min}`);
      } else {
        setError(data.error || 'An error occurred while fetching the prediction.');
      }
    } catch (err) {
      setError('Unable to connect to the server. Please try again later.');
    }
  };

  return (
    <div className="container">
      <h1>Weather Prediction</h1>
      <div className="form-group">
        <label htmlFor="country">Select Country:</label>
        <select
          id="country"
          value={countryName}
          onChange={(e) => setCountryName(e.target.value)}
        >
          <option value="">-- Select a Country --</option>
          {countries.map((country) => (
            <option key={country} value={country}>
              {country.charAt(0).toUpperCase() + country.slice(1)} {/* Capitalize country name */}
            </option>
          ))}
        </select>
        <button onClick={handlePredict}>Get Prediction</button>
      </div>

      {result && (
        <div className="result">
          <h3>Predicted Data</h3>
          <pre>{result}</pre>
        </div>
      )}

      {error && <div className="error">{error}</div>}
    </div>
  );
}

export default App;
