import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import './App.css';

function App() {
  const [locationInput, setLocationInput] = useState('');
  const [resolvedCountry, setResolvedCountry] = useState('');
  const [resolvedTown, setResolvedTown] = useState('');
  const [countries, setCountries] = useState([]);
  const [towns, setTowns] = useState([]);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  // Fetch the list of countries and towns/cities when the component loads
  useEffect(() => {
    const fetchLocations = async () => {
      try {
        // Fetch countries
        const countryResponse = await fetch('http://127.0.0.1:5000/countries');
        const countryData = await countryResponse.json();
        if (countryResponse.ok) {
          setCountries(countryData.countries.map((country) => ({
            value: country,
            label: country.charAt(0).toUpperCase() + country.slice(1),
          })));
        } else {
          setError(countryData.error || 'Failed to fetch countries.');
        }

        // Fetch towns/cities
        const townResponse = await fetch('http://127.0.0.1:5000/towns');
        const townData = await townResponse.json();
        if (townResponse.ok) {
          setTowns(townData.towns.map((town) => ({
            value: town,
            label: town.charAt(0).toUpperCase() + town.slice(1),
          })));
        } else {
          setError(townData.error || 'Failed to fetch towns.');
        }
      } catch (err) {
        setError('Unable to connect to the server. Please try again later.');
      }
    };

    fetchLocations();
  }, []);

  // Handle prediction
  const handlePredict = async () => {
    setResult(null);
    setError(null);

    if (!locationInput && !resolvedTown && !resolvedCountry) {
      setError('Please enter or select a location.');
      return;
    }

    try {
      const response = await fetch(
        `http://127.0.0.1:5000/predict?country=${encodeURIComponent((resolvedTown || resolvedCountry || locationInput).toLowerCase())}`
      );
      const data = await response.json();

      if (response.ok) {
        const { average, max, min } = data.summary;
        setResult(`Predicted data:\nAverage Temperature: ${average}\nMaximum Temperature: ${max}\nMinimum Temperature: ${min}`);

        // Clear inputs and reset dropdowns after successful prediction
        setLocationInput(''); // Clear text input
        setResolvedCountry(''); // Reset country dropdown
        setResolvedTown(''); // Reset town dropdown
      } else {
        setError(data.error || 'Prediction failed.');
      }
    } catch (err) {
      setError('Unable to connect to the server.');
    }
  };

  return (
    <div className="container">
      <h1>Weather Prediction</h1>
      <div className="form-group">
        <label htmlFor="location-input">Enter Location (e.g., Zip Code, GPS, City):</label>
        <input
          type="text"
          id="location-input"
          value={locationInput}
          onChange={(e) => setLocationInput(e.target.value)}
          placeholder="Enter location here"
        />

        <label htmlFor="country-select">Or Select a Country:</label>
        <Select
          id="country-select"
          options={countries}
          value={countries.find((option) => option.value === resolvedCountry)}
          onChange={(selectedOption) => {
            setResolvedCountry(selectedOption?.value || '');
            setResolvedTown(''); // Clear town when a new country is selected
          }}
          placeholder="Select a country..."
          isClearable
        />

        <label htmlFor="town-select">Select or Type a Town/City:</label>
        <Select
          id="town-select"
          options={towns}
          value={towns.find((option) => option.value === resolvedTown)}
          onChange={(selectedOption) => {
            setResolvedTown(selectedOption?.value || '');
            setLocationInput(''); // Clear manual input when a town is selected
          }}
          onInputChange={(inputValue) => setLocationInput(inputValue)} // Update input dynamically
          placeholder="Select or type a town/city..."
          isClearable
        />

        <button onClick={handlePredict}>Get Prediction</button>
      </div>

      {result && (
        <div className="result">
          <h3>Predicted Data</h3>
          <pre>{result}</pre>
        </div>
      )}

      {error && <div className="error">{error}</div>}
    </div>
  );
}

export default App;
