import React, { useState, useEffect } from "react";
import Select from "react-select";
import config from "./config";
import "./App.css";

const { BACKEND_URL } = config;

function App() {
  const [locationInput, setLocationInput] = useState("");
  const [resolvedCountry, setResolvedCountry] = useState("");
  const [resolvedTown, setResolvedTown] = useState("");
  const [countries, setCountries] = useState([]);
  const [towns, setTowns] = useState([]);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  // Fetch countries and towns when the component loads
  useEffect(() => {
    const fetchLocations = async () => {
      try {
        // Fetch countries
        const countryResponse = await fetch(`${BACKEND_URL}/countries`);
        const countryData = await countryResponse.json();
        setCountries(
          countryData.countries.map((country) => ({
            value: country,
            label: country.charAt(0).toUpperCase() + country.slice(1),
          }))
        );

        // Fetch towns
        const townResponse = await fetch(`${BACKEND_URL}/towns`);
        const townData = await townResponse.json();
        setTowns(
          townData.towns.map((town) => ({
            value: town,
            label: town.charAt(0).toUpperCase() + town.slice(1),
          }))
        );
      } catch (err) {
        setError("Unable to fetch locations. Please try again later.");
      }
    };

    fetchLocations();
  }, []);

  // Handle weather prediction request
  const handlePredict = async () => {
    setResult(null);
    setError(null);

    if (!locationInput && !resolvedTown && !resolvedCountry) {
      setError("Please enter or select a location.");
      return;
    }

    try {
      const location = resolvedTown || resolvedCountry || locationInput;
      const response = await fetch(
        `${BACKEND_URL}/predict?country=${encodeURIComponent(location)}`
      );
      const data = await response.json();

      if (response.ok) {
        const { average, max, min } = data.summary;
        setResult(
          `Predicted data:\nAverage Temperature: ${average}\nMaximum Temperature: ${max}\nMinimum Temperature: ${min}`
        );
        // Reset inputs
        setLocationInput("");
        setResolvedCountry("");
        setResolvedTown("");
      } else {
        setError(data.error || "Prediction failed.");
      }
    } catch (err) {
      setError("Unable to connect to the server.");
    }
  };

  return (
    <div className="container">
      <h1>Weather Prediction</h1>
      <div className="form-group">
        <label htmlFor="location-input">Enter Location:</label>
        <input
          type="text"
          id="location-input"
          value={locationInput}
          onChange={(e) => setLocationInput(e.target.value)}
          placeholder="Enter location here"
        />

        <label htmlFor="country-select">Select a Country:</label>
        <Select
          id="country-select"
          options={countries}
          value={countries.find((option) => option.value === resolvedCountry)}
          onChange={(selectedOption) => {
            setResolvedCountry(selectedOption?.value || "");
            setResolvedTown("");
          }}
          placeholder="Select a country..."
          isClearable
        />

        <label htmlFor="town-select">Select or Type a Town/City:</label>
        <Select
          id="town-select"
          options={towns}
          value={towns.find((option) => option.value === resolvedTown)}
          onChange={(selectedOption) => {
            setResolvedTown(selectedOption?.value || "");
            setLocationInput("");
          }}
          onInputChange={(inputValue) => setLocationInput(inputValue)}
          placeholder="Select or type a town/city..."
          isClearable
        />

        <button onClick={handlePredict}>Get Prediction</button>
      </div>

      {result && (
        <div className="result">
          <h3>Predicted Data</h3>
          <pre>{result}</pre>
        </div>
      )}

      {error && <div className="error">{error}</div>}
    </div>
  );
}

export default App;
